deep_extend.ie8.js:
deepExtend = function(out){
  if (out == null)
    out = {};

  var objs = Array.prototype.slice.call(arguments, 1);

  for (var i=0; i < objs.length; i++){
    var obj = objs[i];

    if (obj){
      for (key in obj){
        if (obj.hasOwnProperty(key)){
          if (typeof obj[key] === 'object')
            deepExtend(out[key], obj[key])
          else
            out[key] = obj[key]
        }
      }
    }
  }

  return out;
}

